"use strict";

require("../globals.js");

// Namespace
var proc = {};

/**
 * Process class
 * @class
 * @abstract
 * @param {Kernel} kernel Associated kernel
 * @param {number} pid Assigned process ID
 */
proc.Process = function(kernel, pid) {
    this._kernel = kernel;
    this._pid = pid;
};

/**
 * Returns the process ID
 * @return {number}
 */
proc.Process.prototype.getPID = function() {
    return this._pid;
};

/**
 * Entry point
 * @abstract
 * @return {StatusObject}
 */
proc.Process.prototype.main = function() {
    throw new Error("Not implemented");
};

module.exports = proc;
