"use strict";

// Namespace
var ArrayUtils = {};

/**
 * Returns if two arrays are equal
 * NOTE: Doesn't check object values
 * @static
 * @param {Array} arr1
 * @param {Array} arr2
 * @return {boolean}
 */
ArrayUtils.areEqual = function(arr1, arr2) {
    if (arr1 === arr2) {
        // Referring to same array
        return true;
    }

    if (arr1.length !== arr2.length) {
        // Different lengths
        return false;
    }

    const length = arr1.length;
    var i;
    for (i=0; i<length; ++i) {
        if (Array.isArray(arr1[i])) {
            if (!Array.isArray(arr2[i])) {
                // Both elements aren't arrays
                return false;
            }

            if (!ArrayUtils.areEqual(arr1[i], arr2[i])) {
                // Element arrays don't match
                            console.log(arr1[i], arr2[i]);
                return false;
            }
        }

        if (arr1[i] !== arr2[i]) {
            // Elements don't match
            return false;
        }
    }

    return true;
};

module.exports.ArrayUtils = ArrayUtils;